export default {
    data(){
        return {
            historyItems:JSON.parse(localStorage.getItem('historyItems'))||[]
        }
    },
    watch:
    {
     historyItems(){
        localStorage.setItem('historyItems',JSON.stringify(this.historyItems))
     }
    },
    computed: {
        collecter({obj}) {
            console.log("this is from collecter");
            console.log({...obj});
            console.log("////")
            this.historyItems.push({id:this.historyItems.length+1,...obj})
            localStorage.setItem('historyItems',JSON.stringify(this.historyItems))

        }
    }
};
