export default {
    data(){
        return {
            historyItems:JSON.parse(localStorage.getItem('historyItems'))||[]
        }
    },
    watch:
    {                                        
     historyItems(){                              
        localStorage.setItem('historyItems',JSON.stringify(this.historyItems))
     }
    },
    computed: {
        historyItems(){
            return JSON.parse(localStorage.getItem('historyItems'))||[]
        }
    },
    methods:{
        pushHistory(id,details){
            let newDate=new Date(Date.now()).toDateString();
            this.historyItems.push({id:id,details:details,Time:newDate})
        }
    }
};
