export default {
    data(){
        return {
            currentTime:new Date(),
            Time:{month:this.currentTime.getMonth,day:this.currentTime.getDay},
            historyItems:JSON.parse(localStorage.getItem('historyItems'))||[]
        }
    },
    watch:
    {                                        
     historyItems(){                              
        localStorage.setItem('historyItems',JSON.stringify(this.historyItems))
     }
    },
    computed: {
        currentTime(){
          let currentTime=new Date();
          let month = currentTime.getMonth();
          let day = currentTime.getDay();
          let hours = currentTime.getHours();
          let mins = currentTime.getMinutes();
          return {month:month,day:day,hours:hours,mins:mins};  
        },
        historyItems(){
            return JSON.parse(localStorage.getItem('historyItems'))||[]
        }
    },
    methods:{
        pushHistory(id,details){
            console.log("///")
            this.historyItems.push({id:id,details:details,Time:this})
        }
    }
};
