import "core-js/modules/es.array.push.js";
//Components
import AssignmentAddingFeature from '@/components/AssignmentsComponents/AssignmentAddingFeature.vue';
import AssignmentUpdatingFeature from '@/components/AssignmentsComponents/AssignmentUpdatingFeature.vue';
import AssignmentDeletingFeature from '@/components/AssignmentsComponents/AssignmentDeletingFeature.vue';
import { mapGetters } from 'vuex';
export default {
  components: {
    AssignmentAddingFeature,
    AssignmentUpdatingFeature,
    AssignmentDeletingFeature
  },
  data() {
    return {};
  },
  computed: {
    ...mapGetters(['assignmentItems', 'taskItems', 'employeeItems'])
  },
  assignmentsExisted() {
    if ((JSON.parse(localStorage.getItem('assignmentItems')) || []).length == 0) return false;else return true;
  },
  methods: {
    reloadPage() {
      window.location.reload();
    },
    addAssignment($event) {
      this.assignmentItems.push({
        id: this.assignmentItems.length + 1,
        ...$event
      });
      if (this.assignmentItems.length == 1) this.reloadPage();
      //updating history
      this.historyItems.push({
        id: this.historyItems.length + 1,
        details: `You added a new Assignment with  ${{
          ...$event
        }.responsible} as a resposible`
      });
    },
    updateAssignment($event) {
      //updating history
      this.historyItems.push({
        id: this.historyItems.length + 1,
        details: `You have updated an Assignment whose responsible was ${{
          ...$event
        }.responsible}`
      });
      if ({
        ...$event
      }.id - 1 < this.assignmentItems.length && {
        ...$event
      }.id - 1 >= 0) {
        if ({
          ...$event
        }.responsible != "") this.assignmentItems[{
          ...$event
        }.id - 1].responsible = {
          ...$event
        }.responsible;
        if ({
          ...$event
        }.tasks != "") this.assignmentItems[{
          ...$event
        }.id - 1].tasks = {
          ...$event
        }.tasks;
        if ({
          ...$event
        }.employees != "") this.assignmentItems[{
          ...$event
        }.id - 1].employees = {
          ...$event
        }.employees;
        localStorage.setItem('assignmentItems', JSON.stringify(this.assignmentItems));
      } else {
        alert("you entered a bad id");
      }
    },
    deleteAssignment($event) {
      if ({
        ...$event
      }.id < this.assignmentItems.length && {
        ...$event
      }.id >= 0) {
        //updating history
        this.historyItems.push({
          id: this.historyItems.length + 1,
          details: `You have deleted an Assignment whose responsible is ${this.assignmentItems[{
            ...$event
          }.id].responsible}`
        });
        this.assignmentItems = this.assignmentItems.slice(0, {
          ...$event
        }.id).concat(this.assignmentItems.slice({
          ...$event
        }.id + 1, this.assignmentItems.length));
        this.assignmentItems.filter(el => el.id > {
          ...$event
        }.id).map(el => el.id--);
        localStorage.setItem('assignmentItems', JSON.stringify(this.assignmentItems));
        if ({
          ...$event
        }.id == 0) this.reloadPage();
      } else {
        alert("you entered a bad id");
      }
    }
  }
};
