import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)
  // creating the routes
const routes = [
  {
    path:'/home',
    name:'HomePage',
    component:()=>import('../views/Home/HomePage.vue')
  },
  {
    path:'/employee-page',
    name:'EmployeePage',
    component:()=> import ('../views/Employee/EmployeePage.vue')
  },
  {
    path:'/task-page',
    name:'TaskPage',
    component:()=> import('../views/Task/TaskPage.vue')
  },
  {
    path:'/assignments-page',
    name:'EmployeeAssignmentsPage',
    component:()=> import('../views/Assignments/AssignmentPage.vue')
  }
  ,
  {
    path: '/edit/:id',
    name: 'edit-task',

    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () =>
      import(/* webpackChunkName: "about" */ '../views/Tasks/EditTask.vue'),
  }
]
const router = new VueRouter({
  routes
})

export default router
