import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)
  // creating the routes
const routes = [
  {
    path:'/home',
    name:'HomePage',
    component:()=>import('../views/Home/HomePage.vue')
  },
  {
    path:'/employee-page',
    name:'EmployeePage',
    component:()=> import ('../views/Employee/EmployeePage.vue')
  },
  {
    path:'/task-page',
    name:'TaskPage',
    component:()=> import('../views/Task/TaskPage.vue')
  },
  {
    path:'/assignments-page',
    name:'EmployeeAssignmentsPage',
    component:()=> import('../views/Assignments/AssignmentPage.vue')
  }
  ,
  {
    path: '/edit/:id',
    name: 'edit',
    component: () =>
      import('../views/GlobalViews/globalEditFeature.vue')
  }
]
const router = new VueRouter({
  routes
})

export default router
