import Vue from 'vue'
import Vuex from 'vuex'
import getters from '@/store/getters.js'
import mutations from '@/store/mutations.js'
import actions from '@/store/actions.js'
Vue.use(Vuex)

//root store 
export default new Vuex.Store(
  {
  state: {
    historyItems:JSON.parse(localStorage.getItem('historyItems'))||[]
  },
  getters: getters,
  mutations:mutations,
  actions: actions,
  modules:{
    employeeModule:import('@/store/Modules/employeesModule'),
    tasksModule:'./store/Modules/tasksModule.js', 
    assignmentModule:'./store/Modules/assignmentModule.js'
  },}
)
